import type { Profile } from "@/utils/psn";

export function generateCounts(trophySummary: Profile["trophySummary"]) {
  let countsSvg = "";

  (["platinum", "gold", "silver", "bronze"] as const).forEach(async (item) => {
    countsSvg += /* html */ `
      <div class="count-item">
        <div class="icon">
          <img height="25" width="20" src="${icons[item]}" alt="${item}" />
        </div>
        <div class="count">${trophySummary.earnedTrophies[item] || 0}</div>
      </div>
    `;
  });

  return countsSvg;
}

const platinumBase64 =
  "data:image/png;base64,";

const goldBase64 =
  "data:image/png;base64,";

const silverBase64 =
  "data:image/png;base64,";

const bronzeBase64 =
  "data:image/png;base64,";

const icons = {
  platinum: platinumBase64,
  gold: goldBase64,
  silver: silverBase64,
  bronze: bronzeBase64,
};
